  Future<LeituraModel> findOne() async{
    int tamanho = leitura.length;
    int valor = Random().nextInt(tamanho);
    return Future.delayed(const Duration(microseconds: 6200), () => leitura[valor]);
  }

  Future<List<LeituraModel>> findAll() async {
    return Future.delayed(const Duration(microseconds: 6200), () => leitura);
  }

  
